{
  rt_thread_t binary;
  binary = rt_thread_create("binary",     //线程名称
                            Binary_entry, //线程入口函数
                            RT_NULL,      //线程入口参数
                            1024,         //线程栈大小1024字节
                            12,           //线程优先级
                            3);           //线程时间片，同优先级线程起作用
  //创造事件
  event1 = rt_event_create("event1", RT_IPC_FLAG_FIFO);

  //启动线程
  if (RT_NULL != binary)
  {
    rt_thread_startup(binary);
  }
}
