void Image_Compensate(uint8 *RawImagePtr, uint8 *LightImagePtr)
{
  uint16 buff;
  for (uint8 i = 0; i < IMAGE_H; i++)
  {
    for (uint8 j = 0; j < IMAGE_W; j++)
    {
      buff = *(RawImagePtr + i * IMAGE_W + j) + LightModeBuff[i][j];
      *(LightImagePtr + i * IMAGE_W + j) = (uint8)((buff > 255) ? 255 : buff); //防止溢出
    }
  }
}
/***********************************************************
 * @brief 大津算法
 * @param image 图像地址
 * @param col 图像宽度（范围1~IMAGE_W）
 * @param row 图像高度（范围1~IMAGE_H）
 * @return 阈值
 ***********************************************************/
static inline uint8 OtsuThreshold(uint8 *image, uint16 col, uint16 row)
{
#define GrayScale 256
#define StartGray 90
  uint16 width = col;
  uint16 height = row;
  int pixelCount[GrayScale]; //每个灰度级的像素个数
  float pixelPro[GrayScale]; //每个灰度级的像素概率
  int16 i, j, pixelSum = width * height;
  uint8 threshold = 0;
  uint8 *data = image; //指向像素数据的指针
  for (i = 0; i < GrayScale; i++)
  {
    pixelCount[i] = 0;
    pixelPro[i] = 0.0;
  }
  //统计灰度级中每个像素在整幅图像中的个数
  int grayTmp = 0;
  uint32 graySum = 0; //灰度值和
  for (i = 0; i < height; i += 2)
  {
    for (j = 0; j < width; j += 2)
    {
      grayTmp = (int)data[i * IMAGE_W + j];
      pixelCount[grayTmp]++; //将像素值作为计数数组的下标
      graySum += grayTmp;    //计算图片总灰度值和
    }
  }
  float grayEverage = (float)graySum / pixelSum; //计算图像平均灰度

  //计算每个像素在整幅图像中的比例
  for (i = 0; i < GrayScale / 2; i++)
  {
    pixelPro[i] = (float)pixelCount[i] / pixelSum;
  }

  //遍历灰度级[0,255]
  float w0, w1, u0tmp, u1tmp, u0, u1, deltaTmp, deltaMax = 0;
  w1 = u1tmp = 0;
  for (i = GrayScale - 1; i >= StartGray; i--) // i作为阈值，从后往前算前景
  {
    w0 += pixelPro[i];        //背景部分每个灰度值的像素点所占比列之和，即背景部分的比例
    u0tmp += i * pixelPro[i]; //背景部分，每个灰度值点的比例*灰度值

    w1 = 1 - w0; //背景
    u1tmp = grayEverage - u1tmp;

    u0 = u0tmp / w0; //背景平均灰度
    u1 = u1tmp / w1; //前景平均灰度

    deltaTmp = w0 * w1 * pow((u0 - u1), 2); //计算方差
    if (deltaTmp > deltaMax)
    {
      deltaMax = deltaTmp;
      threshold = i;
    }
  }
  return threshold;
}
/***********************************************************
 * @brief 大津算法，可对图像特定区域计算阈值
 * @param image 图像地址
 * @param Startwidth 起始图像宽度（范围1~IMAGE_W）
 * @param EndWidth 终止图像宽度（范围1~IMAGE_W）
 * @param StartHeight 起始图像高度（范围1~IMAGE_H）
 * @param EndHeight 终止图像高度（范围1~IMAGE_H）
 * @return 阈值
 ***********************************************************/
static inline uint8 Otsu_threshold(uint8 *image, uint16 StartWidth, uint16 EndWidth, uint16 StartHeight, uint16 EndHeight)
{
#define GrayScale 256
#define StartGray 90
  int pixelCount[GrayScale] = {0};   //像素个数
  float pixelPro[GrayScale] = {0.0}; //像素概率
  int i, j, pixelSum = (EndWidth - StartWidth + 1) * (EndHeight - StartHeight + 1);
  uint8 threshold = 0;
  uint8 *data = image; //指向像素数据的指针
  uint32 graySum = 0;  //灰度值和

  //统计灰度级中每个像素在整幅图像中的个数
  int grayTmp = 0;
  for (i = StartHeight - 1; i < EndHeight; i++)
  {
    for (j = StartWidth - 1; j < EndWidth; j++)
    {
      grayTmp = (int)data[i * IMAGE_W + j];
      pixelCount[grayTmp]++; //将像素值作为计数数组的下标
      graySum += grayTmp;    //计算图片总灰度值和
    }
  }
  float grayEverage = (float)graySum / pixelSum; //计算图像平均灰度

  //计算每个像素在整幅图像中的比例
  for (i = 0; i < GrayScale; i++)
  {
    pixelPro[i] = (float)pixelCount[i] / pixelSum;
  }

  //遍历灰度级[0,255]
  float w0, w1, u0tmp, u1tmp, u0, u1, deltaTmp, deltaMax = 0;
  w1 = u1tmp = 0;
  for (i = GrayScale - 1; i >= StartGray; i--) // i作为阈值，从后往前算前景
  {
    w1 += pixelPro[i]; //前景
    u1tmp += i * pixelPro[i];

    w0 = 1 - w1; //背景
    u0tmp = grayEverage - u1tmp;

    u0 = u0tmp / w0;                        //背景平均灰度
    u1 = u1tmp / w1;                        //前景平均灰度
    deltaTmp = w0 * w1 * pow((u0 - u1), 2); //计算方差
    if (deltaTmp > deltaMax)
    {
      deltaMax = deltaTmp;
      threshold = i;
    }
  }

  return threshold;
}
/***********************************************************
 * @brief 迭代法求阈值
 * @param image 图像地址
 * @param col 图像宽度（范围1~IMAGE_W）
 * @param row 图像高度（范围1~IMAGE_H）
 * @return 阈值
 ***********************************************************/
#define ERROR 2 //误差

int16 EdgeThres = 18; //晚上20  白天25 18

float Iteration_Threshould(uint8 *image, uint16 col_w, uint16 row_h)
{
  uint16_t i = 0, j = 0, N0 = 0, N1 = 0, flag = 0;
  float T0, T1, T2, T_center;
  uint32_t S0 = 0, S1 = 0;
  //初始预估值
  float threshold = 70.0;
  T2 = threshold;

  uint8 *data = image; //指向像素数据的指针
  do
  {
    //分割图像为两部分
    for (i = 0; i < row_h; i++)
    {
      for (j = 0; j < col_w; j++)
      {
        if (image[i * col_w + j] < T2)
        {
          S0 += data[i * col_w + j]; // G2部分的像素灰度值之和
          N0++;                      // G2部分的像素点个数
        }
        else
        {
          S1 += data[i * col_w + j]; // G1部分的像素灰度值之和
          N1++;                      // G1部分的像素点个数
        }
      }
    }
    T0 = S0 / N0;             // G2部分的平均灰度值
    T1 = S1 / N1;             // G1部分的平均灰度值
    T_center = (T0 + T1) / 2; //新的阈值
    if (T2 < T_center)
    {
      if ((T_center - T2) > ERROR)
      {
        flag = 1;
      }
      else
      {
        flag = 0;
      }
    }
    else
    {
      if ((T2 - T_center) > ERROR)
      {
        flag = 1;
      }
      else
      {
        flag = 0;
      }
    }
    T2 = T_center;
    threshold = T2;
  } while (flag);
  return threshold;
}
/***********************************************************
 * @brief 图像直方图均衡化（效果不佳）
 ***********************************************************/
void HistEqImageConvert(uint8 *RawImagePtr, uint8 *HistEqImagePtr)
{
  int gray[256] = {0};                //记录每个灰度级别下的像素个数
  float gray_prob[256] = {0};         //记录灰度分布频率
  float gray_distribution[256] = {0}; //记录原始图像的累计密度
  int gray_equal[256] = {0};          //均衡化后的灰度值
  int gray_sum = 0;                   //像素总数
  gray_sum = IMAGE_H * IMAGE_W;

  //统计每个灰度下的像素个数
  for (int i = 0; i < gray_sum; i++)
  {
    gray[RawImagePtr[i]]++;
  }

  //统计原始图像的灰度分布频率
  for (int i = 0; i < 256; i++)
  {
    gray_prob[i] = (float)gray[i] / gray_sum;
  }
  //计算累计密度
  gray_distribution[0] = gray_prob[0];
  for (int i = 1; i < 256; i++)
  {
    gray_distribution[i] = gray_distribution[i - 1] + gray_prob[i];
  }
  //重新计算均衡化后的灰度值，四舍五入。参考公式：(N-1)*T+0.5
  for (int i = 0; i < 256; i++)
  {
    gray_equal[i] = (uint8)(255 * gray_distribution[i] + 0.5); //(int)(a+0.5)
  }
  //直方图均衡化,更新原图每个点的像素值
  for (int i = 0; i < gray_sum; i++)
  {
    HistEqImagePtr[i] = gray_equal[RawImagePtr[i]];
  }
}
/***********************************************************
 * @brief 求取二值化阈值
 * @param type 0:大津法；1：迭代法；2：阳光算法;3:直方图均衡加大津
 * @param raw_iamge 原始图像起始行地址
 * @return
 ***********************************************************/
static inline void Binary_threshold(uint8 type, uint8 *raw_image)
{
  if (type == 0)
  {
    CarInfo.BinaryThreshold = Otsu_threshold(raw_image, 1, IMAGE_W, 25, IMAGE_H - 10);
  }
  if (type == 1)
  {
    CarInfo.BinaryThreshold = Iteration_Threshould(raw_image, IMAGE_W, IMAGE_H);
  }
  if (type == 2)
  {
    uint8 ImageThreshold1 = Otsu_threshold(raw_image, 1, IMAGE_W, 25, IMAGE_H - 10); //计算第一次阈值
    for (int i = 0; i < IMAGE_H; i++)
      for (int j = 0; j < IMAGE_W; j++)
      {
        if (mt9v03x_csi_image[i][j] > ImageThreshold1)
        {
          BinaryImage[i][j] = mt9v03x_csi_image[i][j];
        }
        else
        {
          BinaryImage[i][j] = ImageThreshold1 - 10;
        }
      }
    uint8 ImageThreshold2 = Otsu_threshold(BinaryImage[0], 1, IMAGE_W, 25, IMAGE_H - 10); //计算第二次阈值
    CarInfo.BinaryThreshold = ImageThreshold2;
  }
  if (type == 3)
  {
    uint8 *HistImagePtr = hist_eq_image[0];
    HistEqImageConvert(raw_image, HistImagePtr);
    CarInfo.BinaryThreshold = Otsu_threshold(HistImagePtr, 1, IMAGE_W, 25, IMAGE_H - 10);
  }
}
