/*************************************************************
 * @brief 寻找图像质心中点
 *
 * @return 带回底边中点信息
 *************************************************************/
uint8 barycentre[2];
void find_barycen(uint8 (*img)[IMAGE_W])
{
	int x = 0,y = 0,n = 0;
	for(int j = 0; j<119; j++)
	{
		for(int i = 0; i<187; i++)
		{
			if(img[j][i] == White)
			{
				x += i;
				y += j;
				n++;
			}
		}
	}
	x /= n;
	y /= n;
	barycentre[0] = x;
	barycentre[1] = y;
}
/*************************************************************
 * @brief 寻找图片的定位点
 * @param p 图片二值化数组
 * @return 带回底边中点信息
 *************************************************************/
void locating_point(uint8 (*p)[IMAGE_W])
{
	uint8 right[120] = {0};
	uint8 left[120] = {0};
	for (int j = 119; j > 0; j--)
	{
		for (int i = 0; i < 185; i++)
		{
			if(p[j][i] == Black && p[j][i + 1] == White && p[j][i + 2] == White)//黑白白判断为左边
			{
				left[j] = i + 1;
			}
			if(p[j][i] == White && p[j][i + 1] == Black && p[j][i + 2] == Black)//白黑黑判断为右边
			{
				right[j] = i + 1;
			}
		}
	}
}
/*************************************************************
 * @brief 计算当前车与图片存在的角度
 * @param p 图片二值化数组
 * @return 计算最小角度
 *************************************************************/
double Computing_angle(uint8 (*p)[IMAGE_W])
{
  int i, j;
  int flag, flag1, flag2 = 0;
  double angle; //角度偏差
  uint8 border[120];
  for (i = 0; i < 120; i++)
  {
    border[i] = 0;
  }
  //找顶点
  for (j = 118; j > 0; j--)
  {
    for (i = 1; i < 187; i++)
    {
      if (p[j][i] == White) //找到一个白点
      {
        //八领域判断直角顶点
        if (p[j - 1][i - 1] == Black && p[j - 1][i] == White && p[j - 1][i + 1] == White && p[j][i - 1] == Black && p[j][i + 1] == White && p[j + 1][i - 1] == Black && p[j + 1][i] == Black && p[j + 1][i + 1] == Black)
        {
//          angle_dot[0] = i;
//          angle_dot[1] = j;
          angle = 90.0;
          return angle;
        }
        else //不是直角点作为种子点
        {
          Xsite = i;
          Ysite = j;
          flag = 1;
          break;
        }
      }
    }
    if (flag == 1)
    {
      break;
    }
  }
  for (j = Ysite; j > 0; j--)
  {
    if (p[j][Xsite] == White)
    {
      for (i = Xsite; i < 187; i++)
      {
        if (p[j][i] == White && p[j][i + 1] == Black) //黑白跳变判断为边
        {
          border[j] = i;
          Xsite = i;
          break;
        }
      }
    }
  }
  for (i = 119; i > 0; i--)
  {
    if (border[i] != 0) //找到边开始的行
    {
      flag1 = i; //记录该行
      break;
    }
  }
  for (i = flag1; i > flag1 - 20; i--) //边的长度最长为20
  {
    if (border[i] == 0)
    {
      flag2 = i - 1;
      break;
    }
  }
//  if (flag2 == 0)
//  {
//    angle = atan2(20, border[flag1 - 20] - angle_dot[0]) * 180 / 3.14;
//  }
//  else if (flag2 != 0)
//  {
//    angle = atan2(flag1 - flag2, border[flag2] - angle_dot[0]) * 180 / 3.14;
//  }
  return angle;
}
/*************************************************************
* 前进方向定义：
 *   0
 * 3   1
 *   2
 *************************************************************/
const int dir_front[4][2] = {{0,  -1},
                             {1,  0},
                             {0,  1},
                             {-1, 0}};
const int dir_frontleft[4][2] = {{-1, -1},
                                 {1,  -1},
                                 {1,   1},
                                 {-1,  1}};
const int dir_frontright[4][2] = {{1,  -1},
                                  {1,  1},
                                  {-1, 1},
                                  {-1, -1}};
/*************************************************************
 * @brief 左手迷宫巡线
 * @param uint8 (*img)[IMAGE_W]:存放图像相关数据的指针。
 * @param block_size:自适应阈值的范围。
 * @param clip_value:经验值，防止出现强制分割的现象（一般为2~5）。
 * @param x:种子x坐标。
 * @param y:种子y坐标。
 * @param int pts[ ][2]用来存放边线数据。
 * @param int *num:存放边线像素点的个数。
 * @return 
 *************************************************************/
void findline_lefthand(uint8 (*img)[IMAGE_W], int block_size, int clip_value, int x, int y, int pts[][2], int *num)
{
	int half = block_size / 2;
  int step = 0, dir = 0, turn = 0;
	 while (step < *num && half < x && x < IMAGE_W - half - 1 && half < y && y < IMAGE_H - half - 1 && turn < 4)
	 {
		 int local_thres = 0;
		 for (int dy = -half; dy <= half; dy++)
		 {
			 for (int dx = -half; dx <= half; dx++)
			 {
				 local_thres += PV(img, x + dx, y + dy);
			 }
		 }
		 local_thres /= block_size * block_size;
     local_thres -= clip_value;
		 
		 int current_value = PV(img, x, y);
     int front_value = PV(img, x + dir_front[dir][0], y + dir_front[dir][1]);
     int frontleft_value = PV(img, x + dir_frontleft[dir][0], y + dir_frontleft[dir][1]);
		 if (front_value < local_thres) 
			{
				dir = (dir + 1) % 4;
				turn++;
      }
			else if (frontleft_value < local_thres) 
			{
        x += dir_front[dir][0];
        y += dir_front[dir][1];
        pts[step][0] = x;
        pts[step][1] = y;
        step++;
        turn = 0;
      }
			else 
			{
         x += dir_frontleft[dir][0];
         y += dir_frontleft[dir][1];
         dir = (dir + 3) % 4;
         pts[step][0] = x;
         pts[step][1] = y;
         step++;
         turn = 0;
		  }
	 }
	 *num = step;
}
